def add_example_process(context: IPool, registry: Registry, options: dict):
    """Add meinberlin specific example content."""
    registry = get_current_registry(context)
    # sample locations
    locations = find_service(context, 'locations')
    appstructs = {adhocracy_core.sheets.geo.IMultiPolygon.__identifier__:
                  {'coordinates': POLYGON_ALT_TREPTOW,
                   'administrative_division': 'bezirksregion'},
                  adhocracy_core.sheets.name.IName.__identifier__:
                  {'name': 'alt-treptow'}}
    registry.content.create(IMultiPolygon.__identifier__,
                            parent=locations,
                            appstructs=appstructs,
                            registry=registry)
    # sample organisation
    appstructs = {adhocracy_core.sheets.name.IName.__identifier__:
                  {'name': 'organisation'}}
    registry.content.create(IOrganisation.__identifier__,
                            parent=context,
                            appstructs=appstructs,
                            registry=registry)


def add_debate_process(context: IPool, registry: Registry, options: dict):
    """Add spd specific process."""
    appstructs = {adhocracy_core.sheets.name.IName.__identifier__:
                  {'name': 'engagement_landschaft'}}
    registry.content.create(IProcess.__identifier__,
                            parent=context,
                            appstructs=appstructs)

meinberlin_acm = ACM().deserialize(
    {'principals': ['anonymous',
                    'participant',
                    'moderator',
                    'creator',
                    'initiator',
                    'admin'],
     'permissions': []})


meinberlin_root_meta = root_meta._replace(
    after_creation=(create_initial_content_for_app_root,
                    add_example_process,
                    add_debate_process,
                    adhocracy_core.resources.root.add_example_process
                    ))


def includeme(config):
    """Add resource type to content."""
    add_resource_type_to_registry(meinberlin_root_meta, config)
